import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import {
  Avatar,
  Button,
  Card,
  CardContent,
  CircularProgress,
  Grid,
  Typography,
  Table,
  TableBody,
  TableCell,
  TablePagination,
  TableRow
} from "@material-ui/core";

import { useGoogleLogin } from "@react-oauth/google";
import GoogleServiceSingleton from "../../services/google-service-singleton";
import { useUserInfoSession } from "../header/user-context";
import SeatBookingServices from "../../services/seat-booking-services";

const useStyles = makeStyles(() => ({
  textAlign: {
    textAlign: "right"
  },
  textAlignCenter: {
    textAlign: "center"
  },
  cardContainer: {
    marginBottom: 5
  }
}));

const FlightListOneWay = (props) => {
  const { flightList } = props;
  const classes = useStyles();
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const [loginDone, setLoginDone] = useState(false);
  const [flightSelected, setFlightSelected] = useState("");
  const [departureCity, setDepartureCity] = useState("");
  const [arrivalCity, setArrivalCity] = useState("");
  const [departureDate, setDepartureDate] = useState("");
  const [departureTime, setDepartureTime] = useState("");
  const [arrivalDate, setArrivalDate] = useState("");
  const [arrivalTime, setArrivalTime] = useState("");
  const [randomNumber, setRandomNumber] = useState(Math.random()); // Random number generated once
  const [searchResults, setSearchResults] = useState(flightList?.result || []); // Initialize state with flightList.result

  const history = useHistory();

  // Get session details from cache
  const { userInfoSession, updateUserInfoSession, appendToUserInfoSession } = useUserInfoSession();

  // UseRef to persist searchResults between renders (optional)
  const searchResultsRef = React.useRef(searchResults);

//   // Store the flight search results before login
//   useEffect(() => {
//     if (flightList?.result?.length > 0) {
//       setSearchResults(flightList.result); // Store in state
//       searchResultsRef.current = flightList.result; // Store in ref for persistence
//     }
//   }, [flightList]);
    // Load session data from localStorage if available
    useEffect(() => {
      const sessionData = localStorage.getItem("userSession");
      if (sessionData) {
        // If there's a session in localStorage, parse and set it
        const parsedSession = JSON.parse(sessionData);
        updateUserInfoSession(parsedSession);
      }
    }, [updateUserInfoSession]);

    // Save the updated session to localStorage whenever it changes
    useEffect(() => {
      if (userInfoSession) {
        localStorage.setItem("userSession", JSON.stringify(userInfoSession));
      }
    }, [userInfoSession]);

  const login = useGoogleLogin({
    onSuccess: async (tokenResponse) => {
      try {
        const userInfo = await GoogleServiceSingleton.getUserInfo(tokenResponse.access_token);
        setLoginDone(true);
        updateUserInfoSession({
          ...userInfo,
          selectedFlightNumber: flightSelected,
          departureCity,
          arrivalCity,
          departureDate,
          departureTime,
          arrivalDate,
          arrivalTime,
        });

        const selectFlightData = await SeatBookingServices.selectFlight(
          userInfo.userId,
          flightSelected,
          departureCity,
          arrivalCity,
          departureDate,
          departureTime,
          arrivalDate,
          arrivalTime,
        );
        console.log("Select flight response: ", selectFlightData);

        // Redirect to /seat-selection
        history.push("/seat-selection");
      } catch (error) {
        console.error("Error fetching user info:", error);
      }
    },
  });

  const handleFlightSelection = async (flightDetail) => {
    setFlightSelected(flightDetail.flightNumber);
    setDepartureCity(flightDetail.departure);
    setArrivalCity(flightDetail.arrival);
    setDepartureDate(flightDetail.departureDate);
    setDepartureTime(flightDetail.departureTime);
    setArrivalTime(flightDetail.arrivalTime);
    setArrivalDate(flightDetail.arrivalDate);

    if (!userInfoSession) {
      history.push("/loginpage");
    } else {
      const selectFlightData = await SeatBookingServices.selectFlight(
        userInfoSession.userId,
        flightDetail.flightNumber,
        flightDetail.departure,
        flightDetail.arrival,
        flightDetail.departureDate,
        flightDetail.departureTime,
        flightDetail.arrivalDate,
        flightDetail.arrivalTime,
      );

      console.log("Select flight response: ", selectFlightData);
      appendToUserInfoSession({
        selectedFlightNumber: flightDetail.flightNumber,
        departureCity: flightDetail.departure,
        arrivalCity: flightDetail.arrival,
        departureDate: flightDetail.departureDate,
        departureTime: flightDetail.departureTime,
        arrivalDate: flightDetail.arrivalDate,
        arrivalTime: flightDetail.arrivalTime,
      });
      history.push("/seat-selection");
    }
  };

  let component = null;

  if (flightList?.loading) {
    component = <CircularProgress />;
  } else if (searchResults?.length > 0) {
    component = (
      <Table>
        <TableBody>
          {searchResults
            .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
            .map((val, index) => {
              const randomPrice = val?.minimumSeatPrice + Math.floor(randomNumber * 399) - 199;
              return (
                <TableRow key={index}>
                  <TableCell>
                    <Card className={classes.cardContainer}>
                      <CardContent>
                        <Grid container>
                          <Grid item xs={2}>
                            <Avatar src={val.airlineLogo} alt={val.airlineName} />
                          </Grid>
                          <Grid item xs={2} className={classes.textAlignCenter}>
                            <Typography align="center">{val.departureTime}</Typography>
                            <Typography variant="caption">{val.departure}</Typography>
                          </Grid>
                          <Grid item xs={2} className={classes.textAlignCenter}>
                            <Typography>{val.airlineName}</Typography>
                            <Typography variant="caption">{val.flightNumber}</Typography>
                            <br />
                            <Typography variant="caption">
                              {val.noOfStops === "0" ? `No Stops` : `Non-Stop`}
                            </Typography>
                          </Grid>
                          <Grid item xs={2} className={classes.textAlignCenter}>
                            <Typography>{val.arrivalTime}</Typography>
                            <Typography variant="caption">{val.arrival}</Typography>
                          </Grid>
                          <Grid item xs={4} className={classes.textAlign}>
                            <Button
                              variant="contained"
                              style={{ backgroundColor: "black", color: "white" }}
                              onClick={() => handleFlightSelection(val)}
                            >
                              {`₹ ${randomPrice}`}
                            </Button>
                          </Grid>
                        </Grid>
                      </CardContent>
                    </Card>
                  </TableCell>
                </TableRow>
              );
            })}
        </TableBody>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={searchResults.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={(event, newPage) => setPage(newPage)}
          onChangeRowsPerPage={(event) => {
            setRowsPerPage(parseInt(event.target.value, 10));
            setPage(0);
          }}
        />
      </Table>
    );
  } else if (flightList?.result?.length === 0) {
    component = <Typography>{`No Records Found..`}</Typography>;
  } else if (flightList?.error) {
    component = <Typography>{`Unable to fetch Data...`}</Typography>;
  }

  return (
    <Grid container>
      <Grid item xs={12}>
        {component}
      </Grid>
    </Grid>
  );
};

FlightListOneWay.propTypes = {
  flightList: PropTypes.object,
};

export default FlightListOneWay;
