import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import {
  Avatar,
  Button,
  Card,
  CardContent,
  CircularProgress,
  Grid,
  Typography,
  Table,
  TableBody,
  TableCell,
  TablePagination,
  TableRow,
} from "@material-ui/core";

import { useGoogleLogin } from "@react-oauth/google";
import GoogleServiceSingleton from "../../services/google-service-singleton";
import { useUserInfoSession } from "../header/user-context";
import SeatBookingServices from "../../services/seat-booking-services";

const useStyles = makeStyles(() => ({
  textAlign: { textAlign: "right" },
  textAlignCenter: { textAlign: "center" },
  cardContainer: { marginBottom: 5 },
}));

const FlightListOneWay = (props) => {
  const { initialFlightList, bookNow } = props;
  const classes = useStyles();
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const [loginDone, setLoginDone] = useState(false);
  const [flightList, setFlightList] = useState(initialFlightList);

  const [flightSelected, setFlightSelected] = useState("");
  const [randomNumber, setRandomNumber] = useState(0);

  const history = useHistory();
  const { userInfoSession, updateUserInfoSession, appendToUserInfoSession } =
    useUserInfoSession();

  useEffect(() => {
    if (userInfoSession) {
      setLoginDone(true);
    }
  }, [userInfoSession]);

  useEffect(() => {
    setRandomNumber(Math.random());
  }, []);

  const login = useGoogleLogin({
    onSuccess: async (tokenResponse) => {
      try {
        const userInfo = await GoogleServiceSingleton.getUserInfo(
          tokenResponse.access_token
        );
        setLoginDone(true);

        updateUserInfoSession({
          ...userInfo,
          selectedFlightNumber: flightSelected,
        });

        const selectFlightData = await SeatBookingServices.selectFlight(
          userInfo.userId,
          flightSelected
        );
        console.log("Select flight response: ", selectFlightData);

        history.push("/seat-selection");
      } catch (error) {
        console.error("Error fetching user info:", error);
      }
    },
  });

  const handleFlightSelection = async (flightDetail) => {
    setFlightSelected(flightDetail.flightNumber);

    if (!userInfoSession) {
      login();
    } else {
      const selectFlightData = await SeatBookingServices.selectFlight(
        userInfoSession.userId,
        flightDetail.flightNumber
      );
      console.log("Select flight response: ", selectFlightData);

      appendToUserInfoSession({
        selectedFlightNumber: flightDetail.flightNumber,
      });
      history.push("/seat-selection");
    }
  };

  const renderFlightList = () => {
    if (flightList?.loading) return <CircularProgress />;
    if (flightList?.result?.length > 0) {
      return (
        <Table>
          <TableBody>
            {flightList.result
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((val, index) => {
                const randomPrice =
                  val?.minimumSeatPrice + Math.floor(randomNumber * 399) - 199;
                return (
                  <TableRow key={index}>
                    <TableCell>
                      <Card className={classes.cardContainer}>
                        <CardContent>
                          <Grid container>
                            <Grid item xs={2}>
                              <Avatar
                                src={val.airlineLogo}
                                alt={val.airlineName}
                              />
                            </Grid>
                            <Grid
                              item
                              xs={2}
                              className={classes.textAlignCenter}
                            >
                              <Typography align="center">
                                {val.departureTime}
                              </Typography>
                              <Typography variant="caption">
                                {val.departure}
                              </Typography>
                            </Grid>
                            <Grid
                              item
                              xs={2}
                              className={classes.textAlignCenter}
                            >
                              <Typography>{val.airlineName}</Typography>
                              <Typography variant="caption">
                                {val.flightNumber}
                              </Typography>
                              <br />
                              <Typography variant="caption">
                                {val.noOfStops === "0" ? `No Stops` : `Non-Stop`}
                              </Typography>
                            </Grid>
                            <Grid
                              item
                              xs={2}
                              className={classes.textAlignCenter}
                            >
                              <Typography>{val.arrivalTime}</Typography>
                              <Typography variant="caption">
                                {val.arrival}
                              </Typography>
                            </Grid>
                            <Grid item xs={4} className={classes.textAlign}>
                              <Button
                                variant="contained"
                                style={{
                                  backgroundColor: "black",
                                  color: "white",
                                }}
                                onClick={() => handleFlightSelection(val)}
                              >
                                {`â‚¹ ${randomPrice}`}
                              </Button>
                            </Grid>
                          </Grid>
                        </CardContent>
                      </Card>
                    </TableCell>
                  </TableRow>
                );
              })}
          </TableBody>
          <TablePagination
            rowsPerPageOptions={[5, 10, 25]}
            component="div"
            count={flightList.result.length}
            rowsPerPage={rowsPerPage}
            page={page}
            onPageChange={(event, newPage) => setPage(newPage)}
            onRowsPerPageChange={(event) => {
              setRowsPerPage(parseInt(event.target.value, 10));
              setPage(0);
            }}
          />
        </Table>
      );
    }
    if (flightList?.result?.length === 0)
      return <Typography>{`No Records Found..`}</Typography>;
    if (flightList?.error)
      return <Typography>{`Unable to fetch Data...`}</Typography>;
  };

  return <Grid container>{renderFlightList()}</Grid>;
};

FlightListOneWay.propTypes = {
  initialFlightList: PropTypes.object,
  bookNow: PropTypes.func,
};

export default FlightListOneWay;
