import React, { useState, useEffect } from "react";
import { AppBar, Tabs, Tab, Typography, Toolbar, Button, Menu, MenuItem } from "@material-ui/core";
import { Link, useLocation, useHistory } from "react-router-dom";
import "./header.css";
import { useGoogleLogin } from "@react-oauth/google";
import GoogleServiceSingleton from "../../services/google-service-singleton";
import { useUserInfoSession } from "./user-context";

const Header = ({ tabs, onShowTab }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userInfo, setUserInfo] = useState(null);
  const [anchorEl, setAnchorEl] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);
  const { userInfoSession, updateUserInfoSession, removeUserInfoSession } = useUserInfoSession();

  const history = useHistory();

  useEffect(() => {
    if (userInfoSession) {
      setUserInfo(userInfoSession);
      setIsLoggedIn(true);
      setMenuOpen(false);
      setAnchorEl(null);
    }
  }, [userInfoSession]);

  const login = useGoogleLogin({
    onSuccess: (tokenResponse) => {
      GoogleServiceSingleton.getUserInfo(tokenResponse.access_token).then(
        (userInfo) => {
          setUserInfo(userInfo);
          setIsLoggedIn(true);
          updateUserInfoSession(userInfo);
        }
      );
    },
  });

  const handleLogout = () => {
    removeUserInfoSession();
    setIsLoggedIn(false);
    setUserInfo(null);
    localStorage.removeItem("flightSearchData"); // Clears flight search data
    history.push("/"); // Redirect to homepage or login page
  };

  const handleManage = () => {
    history.push("/manage");
  };

  const handleBook = () => {
    history.push("/");
  };

  const handleHelp = () => {
    history.push("/help");
  };

  const handleMenuOpen = (event) => {
    setMenuOpen(true);
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setMenuOpen(false);
    setAnchorEl(null);
  };

  const LoginMenuHTMLComponent = () => {
    return (
      <Menu
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleMenuClose}
        getContentAnchorEl={null}
        anchorOrigin={{ vertical: "bottom", horizontal: "center" }}
        transformOrigin={{ vertical: "top", horizontal: "center" }}
        PaperProps={{
          style: {
            backgroundColor: "black",
            color: "white",
          },
        }}
      >
        <MenuItem onClick={handleBook} style={{ color: "white" }}>Book Flights</MenuItem>
        <MenuItem onClick={handleManage} style={{ color: "white" }}>Manage Bookings</MenuItem>
        <MenuItem onClick={handleHelp} style={{ color: "white" }}>Help</MenuItem>
        <MenuItem onClick={handleLogout} style={{ color: "white" }}>Sign Out</MenuItem>
      </Menu>
    );
  }

  const location = useLocation();
  const currentTab = tabs.findIndex((tab) => tab.path === location.pathname);

  return (
    <AppBar position="static">
      <Toolbar style={{ backgroundColor: "black" }}>
        <a href={process.env.PUBLIC_URL + "/BonVoyage.jpeg"} target="_blank" rel="noopener noreferrer">
          <img src={process.env.PUBLIC_URL + "/BonVoyage.jpeg"} alt="Logo" style={{ maxHeight: "50px", marginRight: "10px" }} />
        </a>
        <Typography variant="h6" style={{ marginRight: "50px" }}>
          BonVoyage
        </Typography>
        <Tabs value={currentTab} onChange={() => {}} style={{ flex: 1, justifyContent: "center" }}>
          {tabs.map((tab, index) => (
            tab.show !== false && <Tab key={index} label={tab.label} component={Link} to={tab.path} />
          ))}
        </Tabs>
        {isLoggedIn ? (
          <div>
            <Button
              style={{ backgroundColor: "black", color: "white", border: "1px solid white" }}
              onClick={handleMenuOpen}
            >
              {userInfo?.given_name}
            </Button>
            {menuOpen && <LoginMenuHTMLComponent />}
          </div>
        ) : (
          <Button style={{ backgroundColor: "black", color: "white" }} component={Link} to="/loginpage">
            Login/Sign Up
          </Button>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Header;
