import React, { useState } from "react";
import { useGoogleLogin } from "@react-oauth/google";
import GoogleServiceSingleton from "../../services/google-service-singleton";
import { useUserInfoSession } from "./user-context";
import { useHistory } from "react-router-dom";

const LoginSignup = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const { updateUserInfoSession } = useUserInfoSession();
  const history = useHistory();

  const login = useGoogleLogin({
    onSuccess: (tokenResponse) => {
      GoogleServiceSingleton.getUserInfo(tokenResponse.access_token).then(
        (userInfo) => {
          updateUserInfoSession(userInfo);
          history.push("/"); // Navigate to the homepage after successful login
        }
      );
    },
  });

  const handleFormSubmit = (e) => {
    e.preventDefault();
    // Replace with real authentication API call
    if (username === "admin" && password === "1234") {
      updateUserInfoSession({ username });
      history.push("/"); // Navigate to the homepage after successful login
    } else {
      setError("Invalid username or password.");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "0 auto" }}>
      <h2>Login / Sign Up</h2>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            style={{ width: "100%", margin: "10px 0", padding: "8px" }}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: "100%", margin: "10px 0", padding: "8px" }}
          />
        </div>
        {error && <p style={{ color: "red" }}>{error}</p>}
        <button type="submit" style={{ width: "100%", padding: "10px", marginTop: "10px" }}>
          Login
        </button>
      </form>
      <div style={{ margin: "20px 0", textAlign: "center" }}>OR</div>
      <button
        style={{ width: "100%", padding: "10px", backgroundColor: "#4285F4", color: "white" }}
        onClick={() => login()}
      >
        Login with Google
      </button>
    </div>
  );
};

export default LoginSignup;
