import React, { useState, useEffect } from "react";
import { useGoogleLogin } from "@react-oauth/google";
import GoogleServiceSingleton from "../../services/google-service-singleton";
import { useHistory } from "react-router-dom";
import { useUserInfoSession } from "../../components/header/user-context";
import './loginpage.css'; // Import the CSS file for styling

const LoginSignup = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const { updateUserInfoSession, userInfo } = useUserInfoSession(); // Get user info from context
  const history = useHistory();

  // Check if user data exists in localStorage when the component mounts
  useEffect(() => {
    const userSession = localStorage.getItem('userInfo');
    if (userSession) {
      // If session data exists in localStorage, update the context with that data
      const storedUserInfo = JSON.parse(userSession);
      updateUserInfoSession(storedUserInfo);
      console.log("User session restored from localStorage:", storedUserInfo);
      history.push("seat-selection/"); // Redirect to home if user is already logged in
    }
  }, [history, updateUserInfoSession]);

  // Google login function
  const loginWithGoogle = useGoogleLogin({
    onSuccess: (tokenResponse) => {
      GoogleServiceSingleton.getUserInfo(tokenResponse.access_token).then(
        (userInfo) => {
          console.log("Google login successful:", userInfo);
          // Update user session in context
          updateUserInfoSession(userInfo);

          // Store the user session in localStorage
          localStorage.setItem('userInfo', JSON.stringify(userInfo));
          console.log("User session saved to localStorage:", userInfo);

          history.push("/"); // Navigate to the homepage after successful login
        }
      );
    },
  });

  // Handle sign up or login with username and password
  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (username && password) {
      const userInfo = { username }; // In a real case, this would be the user info from the backend

      // Save the session data in context
      updateUserInfoSession(userInfo);

      // Store the user session in localStorage
      localStorage.setItem('userInfo', JSON.stringify(userInfo));
      console.log("User session saved to localStorage:", userInfo);

      history.push("/"); // Redirect to homepage after successful login/signup
    } else {
      setError("Please enter both username and password.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Welcome to BonVoyage</h2>
        <h3>Please Sign In or Sign Up</h3>
        <form onSubmit={handleFormSubmit}>
          <div className="input-group">
            <label>Username:</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              placeholder="Enter your username"
            />
          </div>
          <div className="input-group">
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
            />
          </div>
          {error && <p className="error">{error}</p>}
          <button type="submit" className="login-btn">
            Sign In / Sign Up
          </button>
        </form>
        <div className="divider">OR</div>
        <button className="google-btn" onClick={() => loginWithGoogle()}>
          Sign In with Google
        </button>
      </div>
    </div>
  );
};

export default LoginSignup;
