import React, { useState } from "react";
import { useGoogleLogin } from "@react-oauth/google";
import GoogleServiceSingleton from "../../services/google-service-singleton";
import { useHistory } from "react-router-dom";
import { useUserInfoSession } from "../../components/header/user-context";

const LoginSignup = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const { updateUserInfoSession } = useUserInfoSession();
  const history = useHistory();

  // Google login function
  const loginWithGoogle = useGoogleLogin({
    onSuccess: (tokenResponse) => {
      GoogleServiceSingleton.getUserInfo(tokenResponse.access_token).then(
        (userInfo) => {
          updateUserInfoSession(userInfo);
          history.push("/"); // Navigate to the homepage after successful login
        }
      );
    },
  });

  // Handle sign up or login with username and password
  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (username && password) {
      // Here you can replace this with actual signup/login logic (e.g., API call)
      const userInfo = { username }; // In a real case, this would be the user info from the backend
      updateUserInfoSession(userInfo); // Save the session data
      history.push("/"); // Redirect to homepage after successful login/signup
    } else {
      setError("Please enter both username and password.");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "0 auto" }}>
      <h2>Login / Sign Up</h2>
      <form onSubmit={handleFormSubmit}>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            style={{ width: "100%", margin: "10px 0", padding: "8px" }}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ width: "100%", margin: "10px 0", padding: "8px" }}
          />
        </div>
        {error && <p style={{ color: "red" }}>{error}</p>}
        <button
          type="submit"
          style={{ width: "100%", padding: "10px", marginTop: "10px" }}
        >
          Sign In / Sign Up
        </button>
      </form>

      <div style={{ margin: "20px 0", textAlign: "center" }}>OR</div>

      <button
        style={{
          width: "100%",
          padding: "10px",
          backgroundColor: "#4285F4",
          color: "white",
        }}
        onClick={() => loginWithGoogle()} // Google Login onClick handler
      >
        Sign In with Google
      </button>
    </div>
  );
};

export default LoginSignup;
